import {
  createBrowserRouter,
  RouterProvider,
  Route,
  Outlet,
} from "react-router-dom";
import Register from "./pages/Register";
import Login from "./pages/Login";
import Write from "./pages/Write";
import Home from "./pages/Home";
import Single from "./pages/Single";
import Navbar from "./components/Navbar";
import Footer from "./components/Footer";
import "./style.scss";
import ChatBot from 'react-simple-chatbot';
import { ThemeProvider } from 'styled-components';
import React from 'react';

// Define a custom component for a more interactive response
const CustomWelcome = () => (
  <div>
    <h2>Welcome to our Blog Site!</h2>
    <p>How can I assist you today? ðŸ˜Š</p>
  </div>
);

const CustomMessage = () => (
  <div>
    <p>Thanks for using our chatbot! If you need further assistance, feel free to ask.</p>
  </div>
);

const Layout = () => {
  const theme = {
    background: '#f5f8fb',
    fontFamily: 'Helvetica Neue',
    headerBgColor: '#00B2B2',
    headerFontColor: '#fff',
    headerFontSize: '15px',
    botBubbleColor: '#00B2B2',
    botFontColor: '#fff',
    userBubbleColor: '#fff',
    userFontColor: '#4a4a4a',
  };

  const steps = [
    {
      id: '1',
      message: <CustomWelcome />,
      trigger: '2',
    },
    {
      id: '2',
      options: [
        { value: 1, label: 'Tell me about the site', trigger: '3' },
        { value: 2, label: 'How to create a post?', trigger: '4' },
        { value: 3, label: 'Contact support', trigger: '5' },
        { value: 4, label: 'Other questions', trigger: '6' },
      ],
    },
    {
      id: '3',
      message: 'This site is a blog platform where you can create, edit, and read blog posts. You can also manage your posts and interact with other users.',
      trigger: '7',
    },
    {
      id: '4',
      message: 'To create a post, navigate to the "Write" page from the menu, enter your content, and publish it!',
      trigger: '7',
    },
    {
      id: '5',
      message: 'You can contact support at support@beblog.com.',
      trigger: '7',
    },
    {
      id: '6',
      message: 'Please type your question or concern, and I will do my best to assist you.',
      trigger: '8',
    },
    {
      id: '7',
      component: <CustomMessage />,
      end: true,
    },
    {
      id: '8',
      user: true,
      trigger: '9',
    },
    {
      id: '9',
      message: 'Thank you for your input! I will get back to you shortly.',
      end: true,
    },
  ];

  return (
    <>
      <Navbar />
      <Outlet />
      <Footer />
      <ThemeProvider theme={theme}>
        <ChatBot steps={steps} floating={true} />
      </ThemeProvider>
    </>
  );
};

const router = createBrowserRouter([
  {
    path: "/",
    element: <Layout />,
    children: [
      {
        path: "/",
        element: <Home />,
      },
      {
        path: "/post/:id",
        element: <Single />,
      },
      {
        path: "/write",
        element: <Write />,
      },
    ],
  },
  {
    path: "/register",
    element: <Register />,
  },
  {
    path: "/login",
    element: <Login />,
  },
]);

function App() {
  return (
    <div className="app">
      <div className="container">
        <RouterProvider router={router} />
      </div>
    </div>
  );
}

export default App;
